class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        i = ans = 0
        while i < n:
            start = i
            while i < n + k - 2 and colors[i % n] != colors[(i + 1) % n]:
                i += 1
            ans += max(i - start + 1 - k + 1, 0)
            i += 1
        return ans
