class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int ans=0;
        for(int i=0;i<n;++i)
            a.push_back(a[i]);
        for(int i=0;i<n;){
            int j=i;
            while(j+1<2*n&&a[j]!=a[j+1])
                ++j;
            while(i<=j&&i<n){
                int len=j-i+1;
                ans+=(len>=k);
                ++i;
            }
        }
        return ans;
    }
};
