class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors *= 2  # 环
        ans = 0
        temp = 1  # temp: 以当前位结束的最长交替组长度
        for i in range(1, n * 2):
            if colors[i] != colors[i - 1]:
                temp += 1
            else:
                temp = 1
            if i >= n and temp >= k:
                ans += 1
        return ans
