class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ans = 0;
        int l = 0; // last colors[l] != colors[l + 1]
        for (int r = 0; r <= (n - 1 + k - 2); ++r) {
            if (colors[r % n] == colors[(r + 1) % n]) {
                // printf("r: %d | %d %d\n", r, colors[r % n], colors[(r + 1) % n]);
                l = r + 1;
            }
            if (r - l + 2 >= k) {
                ++ans;
            }
            // printf("%d %d %d\n", l, r, ans);
        }
        return ans;
    }
};
