class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<n;i++){
            colors.push_back(colors[i]);
        }
        // //01234 56789

        // //1-n是中心
        // for(int i=1;i<=n;i++){

        // }

        int alt_count = 0, count = 0;

        vector<int> alt(n, 0);
        for (int i = 0; i < n; ++i) {
            if (colors[i] != colors[(i + 1) % n]) {
                alt[i] = 1;
            }
        }

        for (int i = 0; i < k - 1; ++i) {
            alt_count += alt[i];
        }

        for (int i = 0; i < n; ++i) {
            if (alt_count == k - 1) {
                count++;
            }

            alt_count -= alt[i];
            alt_count += alt[(i + k - 1) % n];
        }

        return count;
    }
};
