class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if k > n:
            return 0
        colors = colors + colors
        def findr(r, cur):
            while r < n + k - 1:
                if colors[r] != colors[r + 1]:
                    r += 1
                    cur += 1
                else:
                    return (r+1, 0)
                if cur == k:
                    return (r, 1)
            return (n+1,0)

        ans = 0
        l, r = 0, 0
        cur = 1
        while l < n:
            while 1:
                r, flag = findr(r, cur)
                if flag:
                    cur = k
                    break
                l = r
                r = l
                cur = 1
                if l >= n:
                    return ans
            ans += 1
            l += 1
            cur -= 1

        return ans
