class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>v;
        int n=colors.size();
        for(auto it:colors)v.push_back(it);
        for(auto it:colors)v.push_back(it);
        vector<int>dp(2*n,0);
        int f=0;
        int prev=1-v[v.size()-1];
        for(int i=2*n-1;i>=0;i--){
            if(prev==v[i]){
                f=1;
                dp[i]=f;
            }
            else{
                f++;
                dp[i]=f;
            }
            prev=v[i];
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(dp[i]>=k)ans++;
        }
        return ans;
    }
};
