class Solution {
 public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int n = colors.size();
    vector<int> a = colors;
    for (int i = 0; i < n; i++) {
      a.push_back(colors[i]);
    }
    vector<int> f(n * 2, 0);
    f[0] = 1;
    for (int i = 1; i < 2 * n; i++) {
      if (a[i] != a[i - 1]) {
        f[i] = f[i - 1] + 1;
      } else {
        f[i] = 1;
      }
    }
    int ans = 0;
    // n + k - 1 - (k - 1) = n
    for (int i = k - 1; i < n + k - 1; i++) {
      if (f[i] >= k) {
        ans++;
      }
    }
    return ans;
  }
};
