class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int blue = 0;
        bool flag = true;
        vector<int>mark(n,0);
        for(int i = 0; i < n; i++){
            int l  = 0;
            int r = 0;
            if(i==0){
                l = colors[n-1];
            }
            else{
                l = colors[i-1];
            }
            if(i==n-1){
                r = colors[0];
            }
            else{
                r = colors[i+1];
            }

            if(l==r && colors[i]!=l){
                mark[i] = 1;
            }
        }


        int c =0 ;
        for(int i = 1; i < k-1; i++){
            c+= mark[i];
        }

        // for(int i = 0;i<n;i++)cout<<mark[i]<<" "; 
        if(c==k-2)ans++;


        int st = 1;
        for(int i = k-1; i < (n+k-2); i++){
            // cout<<st%n<<" "<<i%n<<endl;
            c-=mark[st%n];
            c+=mark[i%n];
            st++;
            if(c==k-2)ans++;

            // cout<<ans << " "<<c<< endl;

        }

        return ans;
    }
};
