class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors
        l = [i for i in range(len(colors) - 1) if colors[i] == colors[i + 1]]
        res = 0
        j = 0
        for i in range(n):
            while j < len(l) and l[j] < i:
                j += 1
            if j == len(l) or l[j] >= i + k - 1:
                res += 1
        return res
