class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        for i in range(k-1):
            colors.append(colors[i])

        groups = [[-1, -2, 0]]  # a, b, length
        for a,b in zip(colors, colors[1:]):
            if (a, b) == (groups[-1][0], groups[-1][1]):
                groups[-1][0], groups[-1][1] = groups[-1][1], groups[-1][0]
                groups[-1][-1] += 1
            else:
                groups.append([b, a, 2])

        # print(groups)
        res = 0
        for a,b,length in groups:
            if a == b:
                continue
            res += max(0, length + 1 - k)
        return res
