class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        for(int i=0;i<(k-1);i++)a.push_back(a[i]);
        vector<int> arr;
        int ans=0,ans1=1;
        for(int i=1;i<a.size();i++)
        {
            if(a[i]!=a[i-1])ans1++;
            else 
            {
                arr.push_back(ans1);
                ans1=1;
            }
        }
        arr.push_back(ans1);
        for(auto x:arr)
        {
            if(x>=k)ans+=x-k+1;
        }
        return ans;
    }
};
