class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        for(int i = 0; i < n; i++) v.push_back(v[i]);
        int cnt{}, ans{};
        for(int i = 1; i <= n + k - 2; i++){
            if(v[i] != v[i - 1]) cnt++;
            else cnt = 0;
            if(cnt >= k - 1) {
                ans++;
            }
        }
        return ans;
    }
};
