class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] a = new int[2 * n];
        for (int i = 0; i < n; i++) {
            a[i] = a[i + n] = colors[i];
        }
        int ans = 0;
        boolean ok = false;
         for (int i = 0; i < n; i++) {
            if (ok) {
                if (a[i + k - 1] != a[i + k - 2]) ans++;
                else {
                    ok = false;
                     i = i + k - 3;
                }
            } else {
                ok = true;
                int idx = -1;
                int last = a[i];
                for (int j = i + 1; j < i + k; j++) {
                    if (a[j] != last) last = a[j];
                    else {
                        ok = false;
                        idx = j;
                        break;
                    }
                }
                if (ok) ans++;
                else {
                    i = idx - 1;
                }
            }


        }
       // System.out.println(ans);
        return ans;
    }
}
