class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        ans = 0
        colors += colors[:k - 1]
        lst = -1
        cl = 0
        for c in colors:
            if c != lst:
                cl += 1
            else:
                if cl >= k:
                    ans += cl - k + 1
                cl = 1    
            lst = c
        if cl >= k:
            ans += cl - k + 1

        return ans
