class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        colors.resize(n * 2);
        for (int i = 0; i < n; i++) {
            colors[i + n] = colors[i];
        }
        int ans = 0;
        for (int i = 0; i < n;) {
            int j;
            for (j = i + 1; j < k + n - 1 && colors[j] != colors[j - 1]; j++);
            int len = j - i;
            // cout << "i=" << i << ", len=" << len << endl;
            if (len >= k) {
                ans += len - k + 1;
            }

            i += len;
        }
        return ans;
    }
};
