class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for (int i = 0; i < n; ++i)
            a.push_back(a[i]);
        int f = 1;
        for (int i = 1; i < k - 1; ++i)
            f = a[i] != a[i - 1] ? f + 1 : 1;
        int ans = 0;
        for (int i = k - 1; i < k + n - 1; ++i) {
            f = a[i] != a[i - 1] ? f + 1 : 1;
            if (f >= k) ++ans;
        }
        return ans;
    }
};
