impl Solution {
    pub fn number_of_alternating_groups(colors: Vec<i32>, k: i32) -> i32 {
        let n = colors.len();
        let mut count = 1;
        let mut result = 0;
        for i in 1..(n + k as usize - 1) {
            if colors[i % n] != colors[(i - 1) % n] {
                count += 1;
                if count >= k {
                    result += 1;
                    // println!("{}", i);
                }
            } else {
                count = 1;
            }
        }
        result
    }
}
