class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0; i < k-1; i++) colors.push_back(colors[i]);
        int ans = 0;
        for(int i = 1, cnt = 1; i < colors.size(); i++){
            cnt = (colors[i] != colors[i-1]) ? cnt+1 : 1;
            ans += (cnt >= k);
        }
        return ans;
    }
};
