class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length, len = n + k - 1;
        int[] a = new int[len];
        for (int i = 0; i < len; i++)
            a[i] = colors[i % n];

        int l = 0, r = 1, ans = 0;
        while (l < len && r < len) {
            r = l + 1;
            while (r < len && a[r] != a[r - 1])
                r++;

            int length = r - l;
            ans += Math.max(0, length - k + 1);
            l = r;
        }
        return ans;
    }
}
