class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        for(int i=0;i<k-1;i++){
           col.push_back(col[i]);
        }
        int curr=0,ans=0;
        for(int i=1;i<col.size();i++){
            if(col[i]!=col[i-1]){
                curr++;
            }
            else{
                curr=0;
            }
            if(curr>=k-1){
                ans++;
            }
        }
        return ans;
    }
};
