class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        ans = 0
        n = len(colors)
        colors += colors
        n *= 2
        pre = [0]*n
        for i in range(1, n):
            if colors[i] != colors[i-1]:
                pre[i] = 1
            else:
                pre[i] = 0
        pp = list(accumulate(pre, initial=0))
        for i in range(n//2):
            if pp[i+k] - pp[i+1] == k-1:
                ans += 1
        return ans
