class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> us=colors;
        us.insert(us.end(),colors.begin(),colors.end());
        us.emplace_back(colors[0]);int ans = 0,pre=1;
        for(int i=1;i<n+k-1;++i){
            if(us[i]==us[i-1]) pre=1;
            else{
                ++pre;
                ans+=(pre>=k);
            }
        }return ans;
    }
};
