class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        ans, count = 0, 2
        for j in range(len(colors)):
            if colors[j] == colors[j-1]:
                break
        else:
            return len(colors)
        for i in range(j, len(colors)+j):
            if colors[i % len(colors)] != colors[(i-1) % len(colors)] and colors[i % len(colors)] != colors[(i+1) % len(colors)]:
                count += 1
            else:
                count = 2
            if count >= k:
                ans += 1
        return ans
