class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int K = k;
        K--;
        int ptr1 = 0;
        while(K--) {
            colors.push_back(colors[ptr1]);
            ptr1++;
        }
        int n = colors.size();
        int cnt = 0;
        int j = 1, i = 0;
        while (j<n) {
            if (j-i+1 == k) {
                if (colors[j] == colors[j-1]) {
                    i = j;
                } else {
                    cnt++;
                    i++;
                }
            } else {
                if (colors[j] == colors[j-1]) {
                    i = j;
                }
            }
            j++;
        }
        return cnt;
    }
};
