class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        auto v = colors;
        for(int i = 0; i < k - 1; i++){
            v.push_back(v[i]);
        }
        int n = v.size();
        vector<int> cnt(n); cnt[0] = 1;
        for(int i = 1; i < n; i++){
            if(v[i] != v[i - 1]) cnt[i] = cnt[i - 1] + 1;
            else cnt[i] = 1;
            if(i >= k - 1 && cnt[i] >= k) ans++;
        }

        return ans;
    }
};
