class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int sz=colors.size();
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int n=colors.size();
        int ans=0;
        int cur_sum=0;
        for(int i=1;i<k;i++){
          if(colors[i]==colors[i-1]){
              cur_sum++;
          }
        }
        if(cur_sum==0){
            ans++;
        }
        for(int i=k;i<n;i++){
            if(colors[i]==colors[i-1]){
                cur_sum++;
            }
            if(colors[i-k]==colors[i-k+1]){
                cur_sum--;
            }
            if(cur_sum==0){
                ans++;
            }
        }
        return ans;
    }
};
