class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for (int i = 0; i < n - 1; i++) {
          a.push_back(a[i]);
        }

        vector<int> dp(a.size(), 1);
        for (int i = 1; i < a.size(); i++) {
          if (a[i] != a[i - 1]) {
            dp[i] = dp[i - 1] + 1;
          }
        }

        int ans = 0;
        for (int i = n - 1; i < dp.size(); i++) {
          // cout << "i: " << i << " dp[i]: " << dp[i] << endl;
          ans += (dp[i] >= k);
        }

        return ans;
    }
};
