class Solution {
  public int numberOfAlternatingGroups(int[] colors, int k) {
    int len = colors.length;
    int count = 0;
    int[] arr = new int[len];
    for (int i = 0; i < len; i++) {
      int next = (i + 1) % len;
      if (colors[i] == colors[next]) {
        arr[i] = 1;
      }
    }
    int x = 0;
    for (int i = 0; i < k - 1; i++) {
      x += arr[i];
    }
    count += x == 0 ? 1 : 0;
    for (int i = 1; i < len; i++) {
      x -= arr[i - 1];
      x += arr[(i + k - 2) % len];
      if (x == 0) {
        count++;
      }
    }
    return count;
  }
}
