class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:


        n = len(colors)
        extended_colors = colors + colors[:k-1]

        count = 0
        start = 0
        alternating_length = 1

        for end in range(1, len(extended_colors)):
            if extended_colors[end] != extended_colors[end - 1]:
                alternating_length += 1
            else:
                alternating_length = 1
                start = end

            if alternating_length == k:
                count += 1
                alternating_length -= 1

        return count
