from itertools import accumulate
from typing import List


class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors
        for i,v in enumerate(colors):
            if v == 0:
                colors[i] = -1
        pre = 0
        l,r = 0,0
        ans = 0
        while l < n:
            if r - l + 1 < k:
                if pre == colors[r]:
                    l = r
            if r - l + 1 == k:
                if pre == colors[r]:
                    l = r
                else:
                    ans += 1
                    l += 1
            pre = colors[r]
            r += 1
        return ans
