#include<bits/stdc++.h>
using namespace std;

#define ff              first
#define ss              second
#define pb              push_back
#define MOD             1000000007
#define inf             1e18

typedef long long int lli;

class Solution {
public:
	int numberOfAlternatingGroups(vector<int>& colors, int k) {
		vector<int> v;
		int n = colors.size();
		for (int i = 0; i < n; i++)      {
			v.pb(colors[i]);
		}
		for (int i = 0; i < k -1; i++)      {
			v.pb(colors[i]);
		}

		int ret = 0;
		deque<int> dq;
		for (int i = 0; i < v.size(); i++) {
			if (!dq.empty() && dq.back() == v[i]){
                dq.clear();
            }
			dq.push_back(v[i]);
			while (dq.size() > k) dq.pop_front();
			ret += dq.size() == k;
            // if(dq.size() == k){
            //     cout << i << endl;
            // }
		}

		return ret;
	}
};


