class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        j = 1
        n = len(colors)
        for i in reversed(range(n - 1)):
            if colors[i] != colors[i + 1]:
                j += 1
            else:
                break

        ret = 0 if j < k else 1

        for i in range(n - 1):
            if colors[i] != colors[i - 1]:
                j += 1
            else:
                j = 1

            if j >= k:
                ret += 1

        return ret
