class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int>sum(n + 1);
        k--;
        for(int i = 0;i < n;i++){
            int p = i == 0 ? 0 : sum[i-1];
            sum[i] = p + (colors[i] != colors[(i - 1 + n) % n]);
            //cout << sum[i] << ' ';
        }

        int ans = 0;
        for(int i = 0;i < n;i++){
            if(i + k < n){
                if(sum[i + k]-sum[i] == k)ans++;
            }else{
                int cur = k - (n - i);
                if(sum[n-1] - sum[i] + sum[cur] == k)ans++;
            }
            //cout << ans << ' ';
        }
        return ans;
    }
};
