class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        const int n = (int)colors.size();
        vector<int> alt(n, 0);
        for (int i = 0; i < n; ++i) {
            if (colors[i] != colors[(i+n-1)%n]
                && colors[i] != colors[(i+1)%n]) {
                alt[i] = 1;
            }
        }
        int sum = 0;
        for (int i = 1; i < k - 1; ++i) {
            sum += alt[i];
        }
        int l = 0;
        int r = k - 1;
        int ans = 0;
        for (; l < n; ++l) {
            if (sum == k - 2) {
                ++ans;
            }
            sum -= alt[(l+1)%n];
            sum += alt[r];
            r = (r + 1) % n;
        }
        return ans;
    }
};
