class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if k == 1:
            return n
        alt = [0] * n
        for i in range(n):
            if colors[i] != colors[(i + 1) % n]:
                alt[i] = 1
        tot = sum(alt[:k-1])
        ans = 0
        for i in range(n):
            if tot == k-1:
                ans += 1
            tot -= alt[i]
            tot += alt[(i + k - 1) % n]
        return ans
