class Solution:

    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        clrs = colors + colors
        # print(clrs)

        lens = [1]
        begins = [-1]

        for i in range(1, 2 * n):
            if clrs[i] == clrs[i - 1]:
                if lens[-1] == 1:
                    continue
                else:
                    begins.append(-1)
                    lens.append(1)
            else:
                if lens[-1] == 1:
                    begins[-1] = i - 1
                lens[-1] += 1

        # print(begins)
        # print(lens)
        ans = 0
        for b, l in zip(begins, lens):
            if b > n:
                break
            if l < k:
                continue
            ans += min(l - k + 1,n - b)
        return ans
