class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), f[n + k]; f[0] = 1;
        int res = 0;
        for (int i = 1; i < n + k - 1; ++i) {
            f[i] = (colors[i % n] != colors[(i - 1) % n]) ? f[i - 1] + 1 : 1;
            if (f[i] >= k) ++res;
        }
        return res;
    }
};
