class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors *= 2
        res = 0
        count = 1
        for i in range(1, 2 * n):
            if colors[i] != colors[i - 1]:
                count += 1
            else:
                count = 1
            if count >= k and i >= n:
                res += 1
        return res
