class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,n=colors.size(),j=0;
        for(int i=0;i<n;++i){
            colors.push_back(colors[i]);
        }
        for(int i=0;i<n;++i){
            j=max(j,i);
            while(j-i+1<k){
                if(colors[j]==colors[j+1]){
                    i=j;
                    break;
                }
                j++;
            }
            if(j-i+1==k) ans++;
        }
        return ans;
    }
};
