class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v = colors;

        for(auto i : colors){
            v.push_back(i);
        }

        set<int> s;
        for(int i = 1 ; i < v.size() ; i++){
            if(v[i] == v[i-1]){
                s.insert(i);
            }
        }
        int ans = 0;
        for(int i = 0 ; i < colors.size() ; i++){
            auto it = s.upper_bound(i);

            if((it == s.end()) || ((*it) - i >= k)){
                ans++;
            }
        }

        return ans;

    }
};
