class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int col = colors[0];
        int cnt = 1;
        int res = 0;
        for(int i = 0; i < k; i ++) colors.push_back(colors[i]);
        n += k;
        for(int i = 1; i < n; i ++) {
            if(cnt == k) res ++, cnt = k-1;
            if(colors[i] == 1 - col) cnt ++, col = 1 - col;
            else {
                col = colors[i];
                cnt = 1;
            }
        }
        return res;
    }
};
