class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++) colors.push_back(colors[i]);
        int ans=0;
        int n=colors.size();
        int l=1;
        for(int i=n-2;i>=0;i--){
            if(colors[i+1]==colors[i]) l=1;
            else l++;
            if(l>=k) ans++;
        }
        return ans;
    }
};
