class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        deque<pair<int,int>> dq;
        auto add = [&](int x, int i) {
            if (dq.size() && dq.back().first == x) {
                dq.clear();
            }
            dq.push_back({x, i});
        };

        for (int i = 0; i < k; i++) add(colors[i], i);
        int l = 0;
        for (int r = k; r < k + colors.size(); r++) {
            int ridx = r % colors.size();
            if (dq.size() == k) ans++;
            if (dq.front().second == l) dq.pop_front();
            l++; add(colors[ridx], ridx);
        }
        return ans;
    }
};
