func numberOfAlternatingGroups(colors []int, k int) int {
    n := len(colors)
    diff := make([]int, n)
    for i := 0; i < n; i++ {
        if colors[i] == colors[(i + 1) % n] {
            continue
        }
        diff[i] = 1
    }
    cur := 0
    for i := 0; i < k - 1; i++ {
        cur += diff[i]
    }
    res := 0
    for i := 0; i < n; i++ {
        if cur == k - 1 {
            res++
        }
        cur -= diff[i]
        cur += diff[(i + k - 1) % n]
    }
    return res

}
