class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> arr;
        for(auto x : colors) arr.push_back(x);
        for(int i=0;i<k-1;i++) arr.push_back(colors[i]);
        int ans = 0;
        int i = 0;
        queue<int> q;
        int n = arr.size();
        q.push(1);
        while(i<n && q.size()){
            int cnt = 1;
            int j = i+1;
            while(j<n && q.size()){
                if(arr[j] != arr[j - 1]) cnt++;
                else {
                    i = j - 1;
                    break;
                }
                if(j == n - 1) {
                    i = 3 * n;
                    break;
                }
                q.push(1);
                j++;
            }
            q.push(1);
            if(cnt-k+1>0) ans += cnt-k+1;
            i++;
        }
        return ans;
    }
};
