class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& ar, int k) {
        int n = ar.size();
        vector<int> br = ar;
        for(int i = 0; i < k-1; i++) {
            br.push_back(ar[i]);
        }
        int x = br.size();
        int v[x];
        v[x-1] = 1;
        for(int i = x-2; i >= 0; i--) {
            if(br[i] != br[i+1]) {
                v[i] = v[i+1]+1;
            } else {
                v[i] = 1;
            }
        }
        int ans = 0;
        for(int i: v) {
            if(i >= k) ans++;
        }
        return ans;
    }
};
