func numberOfAlternatingGroups(colors []int, k int) int {
	n := len(colors)
	colors = append(colors, colors...)
	dp := make([]int, n*2)
	for i := 0; i < k-1; i++ {
		dp[i] = 1
		if i-1 >= 0 && colors[i] != colors[i-1] {
			dp[i] += dp[i-1]
		}
	}
	ans := 0
	for i := k - 1; i < len(colors) && i-k+1 < n; i++ {
		dp[i] = 1
		if i-1 >= 0 && colors[i] != colors[i-1] {
			dp[i] += dp[i-1]
		}
		if dp[i] >= k {
			ans++
		}
	}
	return ans
}
