class Solution
{
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k)
    {
        int ans = 0;
        queue<int> q;

        for (int i = 0; i < k - 1;i++){
            colors.push_back(colors[i]);
        }

        // q.push(colors[0]);
        for (int i = 0; i < colors.size();i++){
            if(q.empty() || colors[i]!=colors[i-1]){
                q.push(colors[i]);
            }
            else{
                while (!q.empty())
                {
                    q.pop();
                }
                q.push(colors[i]);
            }
            if(q.size()==k){
                ans++;
                q.pop();
            }
        }

        return ans;
    }
};
