class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> pref(2*n, 1);
        int ans=0;
        for(int i=1; i<2*n; i++){
            pref[i]=pref[i-1]+(colors[i%n]!=colors[(i-1)%n]?1:-pref[i-1]+1);
            if(i>=n) ans+=pref[i]>=k;
        }
        return ans;
    }
};
